package com.area.server.minhangareaserver.service.impl;import com.area.server.minhangareaserver.dao.TradeDataMapper;import com.area.server.minhangareaserver.pojo.Trade;import com.area.server.minhangareaserver.pojo.TradeData;import com.area.server.minhangareaserver.service.SysTradeDataService;import com.github.pagehelper.util.StringUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.text.ParseException;import java.util.Date;import java.util.List;import java.util.Map;@Service@Transactionalpublic class SysTradeDataServiceImpl implements SysTradeDataService {	@Autowired	private TradeDataMapper tradeDataMapper;	public List<Map<String, Object>> getDataList(final int page,			final int pageSize, final Map<String, String> map) {		return tradeDataMapper.getDataList(page, pageSize, map);	}	private Trade getTradeIdByName(List<Trade> tradeList, String tradeName) {		Trade result = null;		for (Trade trade : tradeList) {			if (tradeName.equals(trade.getTradename())) {				result = trade;				break;			}		}		return result;	}	public List<String> getAllIndustry(final int areaId) {		return tradeDataMapper.getAllIndustry(areaId);	}	}