package com.area.server.minhangareaserver.service.impl;import com.area.server.minhangareaserver.dao.TradeMapper;import com.area.server.minhangareaserver.pojo.Trade;import com.area.server.minhangareaserver.service.SysTradeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;import java.util.Map;@Service@Transactionalpublic class SysTradeServiceImpl implements SysTradeService {		@Autowired	private TradeMapper tradeMapper;	public List<Map<String, Object>> getDataList(final int page, final int pageSize) {		return tradeMapper.getDataList(page, pageSize);	}	public Integer getDataCount() {		return tradeMapper.getDataCount();	}		public void saveData(Trade trade) {		tradeMapper.saveData(trade);	}	public void editData(Trade trade) {		tradeMapper.editData(trade);	}		public Trade getDataById(final int dataId) {		return tradeMapper.getDataById(dataId);	}		public void deleteDataById(final int id) {		tradeMapper.deleteDataById(id);	}	@Override	public List<Trade> getAllTradesByAreaId(int areaId) {		return tradeMapper.getAllTradesByAreaId(areaId);	}}