taService.getDataByKeys(areaId, tradeId, industry, dateStart, dateEnd);				String[] title = {"区域", "商圈", "行业", "月份", "交易金额（元）", "交易笔数（笔）", "商户数（个）"};				/**		 * 以下为生成Excel操作		 */		// 1.创建一个workbook，对应一个Excel文件		HSSFWorkbook wb = new HSSFWorkbook();		// 2.在workbook中添加一个sheet，对应Excel中的一个sheet		HSSFSheet sheet = wb.createSheet("商圈数据");		sheet.setDefaultColumnWidth(15);		// 3.在sheet中添加表头第0行，老版本poi对excel行数列数有限制short		HSSFRow row = sheet.createRow((int) 0);		// 4.创建单元格，设置值表头，设置表头居中		HSSFCellStyle style = wb.createCellStyle();		HSSFDataFormat format = wb.createDataFormat(); 		//这样才能真正的控制单元格格式，@就是指文本型，具体格式的定义还是参考上面的原文吧 		style.setDataFormat(format.getFormat("@")); 		// 居中格式		style.setAlignment(HSSFCellStyle.ALIGN_CENTER);				// 设置表头		for (int i = 0; i < title.length; i++) {			HSSFCell cell = row.createCell(i);			cell.setCellValue(title[i]);			cell.setCellStyle(style);		}				int cellIndex = 1;		for (Map<String, Object> map : datas) {			row = sheet.createRow(cellIndex);			for (int i = 0; i < title.length; i++) {				HSSFCell cell = row.createCell(i);				if (i == 0) {					cell.setCellValue(map.get("areaName")+"");				}				else if (i == 1) {					cell.setCellValue(map.get("tradeName")+"");				}				else if (i == 2) {					cell.setCellValue(map.get("industry")+"");				}				else if (i == 3) {					cell.setCellValue(map.get("dateTime")+"");				}				else if (i == 4) {					BigDecimal bigDecimal = new BigDecimal((Double)map.get("sales"));					String result = bigDecimal.toString();					cell.setCellValue(result);				}				else if (i == 5) {					cell.setCellValue(" "+map.get("salesNums"));				}				else if (i == 6) {					cell.setCellValue(" "+map.get("mids"));				}				else {					cell.setCellValue("");				}				cell.setCellStyle(style);			}			cellIndex++;		}				String fileName = "商圈数据";		ByteArrayOutputStream os = new ByteArrayOutputStream();		wb.write(os);		byte[] content = os.toByteArray();		InputStream is = new ByteArrayInputStream(content);		// 设置response参数，可以打开下载页面		rep.reset();		rep.setContentType("application/vnd.ms-excel;charset=utf-8");		rep.setHeader("Content-Disposition", "attachment;filename="				+ new String((fileName + ".xls").getBytes(), "iso-8859-1"));		ServletOutputStream out = rep.getOutputStream();		BufferedInputStream bis = null;		BufferedOutputStream bos = null;		try {			bis = new BufferedInputStream(is);			bos = new BufferedOutputStream(out);			byte[] buff = new byte[2048];			int bytesRead;			while (-1 != (bytesRead = bis.read(buff, 0, buff.length))) {				bos.write(buff, 0, bytesRead);			}		} catch (Exception e) {			e.printStackTrace();		} finally {			if (bis != null)				bis.close();			if (bos != null)				bos.close();		}	}		/**	 * 导入数据板块	 * 	 * @param model	 * @return	 */	@RequestMapping(value = "/importView", method = RequestMethod.GET)	public String importView(Model model) {		List<Area> areaList = sysAreaService.getAllDataList();		model.addAttribute("areaList", areaList);		return "sysTradeDataImport";	}		/**	 * 导入数据保存	 * @param req	 * @param rep	 * @return	 * @throws ParseException	 * @throws IOException 	 */	@RequestMapping(value = "/importSave.do", method = RequestMethod.POST)	@ResponseBody	public Map<String, Object> importSave(HttpServletRequest req,			HttpServletResponse rep) throws ParseException, IOException {		Map<String, Object> result = new HashMap<String, Object>();				String areaId = req.getParameter("areaId");		String importFile = req.getParameter("importFile");		// 获取临时保存文件的地址		ResourceBundle resource = ResourceBundle.getBundle("application");		String fileName = importFile;		String path = resource.getString("common.file.path") + fileName;				// 1、读取文件内容		List<List<Object>> list = ReadExcel.readExcel(new File(path));		// 2、获得这个区域的所有商圈		List<Trade> tradeList = sysTradeService.getAllTradesByAreaId(Integer.parseInt(areaId));		// 3、存储到数据库中		sysTradeDataService.saveImport(list, areaId, tradeList);				result.put("code", 0);		result.put("msg", "操作成功！");		return result;	}}