package com.usx.dao.impl;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import com.usx.dao.SysAreaDao;import com.usx.model.Area;import com.usx.util.HibernateUtil;import com.usx.util.StringUtil;@Repositorypublic class SysAreaDaoImpl implements SysAreaDao {		@Autowired	private HibernateUtil hibernateUtil;	@SuppressWarnings("unchecked")	@Override	public List<Area> getDataList(int page, int pageSize) {		String query = "SELECT a.id, a.code, a.areaName  FROM area a limit "				+ (page - 1) * pageSize + ", " + pageSize;		List<Object[]> objects = hibernateUtil.fetchAll(query);		List<Area> resultList = new ArrayList<Area>();		for (Object[] object : objects) {			int id = (Integer) object[0];			String code = (String) object[1];			String areaName = (String) object[2];			Area area = new Area();			area.setId(id);			area.setCode(code);			area.setAreaName(areaName);			resultList.add(area);		}		return resultList;	}	@SuppressWarnings("unchecked")	@Override	public int getDataCount() {		String query = "SELECT count(id) from area";		List<Object> objects = hibernateUtil.fetchAll(query);		Object _count = objects.get(0);		return Integer.parseInt(_count.toString());	}		public List<Area> getAllDataList() {		return hibernateUtil.fetchAll(Area.class);	}		@Override	public void saveData(Area area) {		hibernateUtil.create(area);	}		public void editData(Area area) {		hibernateUtil.update(area);	}		public Area getDataById(final int dataId) {		return hibernateUtil.fetchById(dataId, Area.class);	}		public void deleteDataById(final int id) {		hibernateUtil.delete(id, Area.class);	}}