package com.usx.service.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.usx.dao.SysAreaDao;import com.usx.model.Area;import com.usx.service.SysAreaService;@Service@Transactionalpublic class SysAreaServiceImpl implements SysAreaService {		@Autowired	private SysAreaDao sysAreaDao;	@Override	public List<Area> getDataList(int page, int pageSize) {		return sysAreaDao.getDataList(page, pageSize);	}	@Override	public int getDataCount() {		return sysAreaDao.getDataCount();	}		public List<Area> getAllDataList() {		return sysAreaDao.getAllDataList();	}		@Override	public void saveData(Area area) {		sysAreaDao.saveData(area);	}		public void editData(Area area) {		sysAreaDao.editData(area);	}		public Area getDataById(final int dataId){		return sysAreaDao.getDataById(dataId);	}		public void deleteDataById(final int id) {		sysAreaDao.deleteDataById(id);	}}