package com.usx.service.impl;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.usx.dao.SysUserDao;import com.usx.model.User;import com.usx.service.SysUserService;@Service@Transactionalpublic class SysUserServiceIpml implements SysUserService {		@Autowired	private SysUserDao sysUserDao;		public List<Map<String, Object>> getDataList(final int page, final int pageSize) {		return sysUserDao.getDataList(page, pageSize);	}	public int getDataCount() {		return sysUserDao.getDataCount();	}		/**	 * 保存用户	 * @param sysRole	 */	public void saveData(User user) {		sysUserDao.saveData(user);	}	public void editData(User user) {		sysUserDao.editData(user);	}		public User getDataById(final int dataId) {		return sysUserDao.getDataById(dataId);	}		public void deleteDataById(final int id) {		sysUserDao.deleteDataById(id);	}		public User getUserByName(final String loginName) {		return sysUserDao.getUserByName(loginName);	}}